import {addProject, getProjects} from "../api/api";
import {stopSubmit} from "redux-form";

const SET_NEW_PROJECT = 'SET-NEW-PROJECT';
const SET_ALL_PROJECTS = 'SET-ALL-PROJECTS';

let initialState = {
    projects: [],
    id: 1
};

const projectsReducer = (state = initialState, action) => {
    switch (action.type) {
        case SET_NEW_PROJECT:
            let newProject = {
                id: state.id++,
                name: action.name,
            };
            return {
                ...state,
                projects: [...state.projects, newProject]
            };
        case SET_ALL_PROJECTS:
            return {
                ...state,
                projects: [...state.projects, action.projects]
            };
        default:
            return state;
    }
};

export default projectsReducer;

export const setNewProject = (name) => ({type: SET_NEW_PROJECT, name});
export const setAllProjects = (projects) => ({type: SET_ALL_PROJECTS, projects});

export const addNewProject = (name) => (dispatch) => {
    addProject(name)
        .then((response => {
            if (response === null) {
                dispatch(setNewProject(name));
            }
            else {
                alert("error");
            }
        }))
};
export const getMyProjects = () => (dispatch) => {
    getProjects()
        .then((response => {
                dispatch(setAllProjects(projects));
        }))
};
