import {addProject, getProjects} from "../api/api";
import {stopSubmit} from "redux-form";

const SET_NEW_PROJECT = 'SET-NEW-PROJECT';
const SET_ALL_PROJECTS = 'SET-ALL-PROJECTS';
const INITIALIZED_SUCCESS = 'INITIALIZED-SUCCESS';
const SET_PROJECT_NAMES = 'SET-PROJECT-NAMES';

let initialState = {
    projects: [],
   /* id: 1,*/
    initialized: false,
    names: []
};

const projectsReducer = (state = initialState, action) => {
    switch (action.type) {
        case SET_NEW_PROJECT:
            let newProject = {
               /* id: state.id++,*/
                name: action.name,
            };
            /*debugger;*/
            return {
                ...state,
                projects: [...state.projects, newProject]
            };
        case SET_ALL_PROJECTS:
            return {
                ...state,
                projects: action.projects
            };
        case INITIALIZED_SUCCESS:
            return {
                ...state,
                initialized: true
            };
        case SET_PROJECT_NAMES:
            return {
                ...state,
                names: [...state.names, action.names]
            };
        default:
            return state;
    }
};

export default projectsReducer;

export const setNewProject = (name) => ({type: SET_NEW_PROJECT, name});
export const setAllProjects = (projects) => ({type: SET_ALL_PROJECTS, projects});
export const initializedSuccess = () => ({type: INITIALIZED_SUCCESS});
export const setProjectsNames = (names) => ({type: SET_PROJECT_NAMES, names});

export const addNewProject = (name) => (dispatch) => {
    addProject(name)
        .then((response => {
            if (response === null) {
                dispatch(setNewProject(name));
            }
            else {
                alert("error");
            }
        }))
};
export const getMyProjects = () => (dispatch) => {
   /* debugger;*/
    return getProjects()
        .then((response => {
            let names = response.map(object => object.name);
                dispatch(setProjectsNames(names));
                dispatch(setAllProjects(response));///project.id, projects.name
        }))
};

export const initializeProjects = () => (dispatch) => {
    let promise = dispatch(getMyProjects());
    Promise.all([promise])
        .then(() => {
            dispatch(initializedSuccess());
        });
};
