/*
import axios from "axios";

/!*const instance = axios.create({
    baseURL: 'http://valerystatinov.me/',
});*!/

export const tasksAPI = {
    addTask(name, description, projectId, priority) {
        return axios.post(`http://valerystatinov.me/tasks/`, { name, description, projectId, priority });
    }
};*/
export async function addTask(name, description, projectId, priority) {
    const response = await post('http://valerystatinov.me/tasks/', {name, description, projectId, priority});
    if (response.status === 200) {
        return null;
    }
    return response.json();
}
