import {addTask, tasksAPI} from "../api/api";
import {stopSubmit} from "redux-form";

const SET_NEW_TASK = 'SET-NEW-TASK';
const NAME_SORT = 'NAME-SORT';
const PRIORITY_SORT = 'PRIORITY-SORT';
/*const DELETE_TASK = 'DELETE-TASK';*/

let initialState = {
    posts: [],
    id: 1,
    projectId: 1
};

const tasksReducer = (state = initialState, action) => {
    switch (action.type) {
        case SET_NEW_TASK:
            let newTask = {
                id: state.id++,
                name: action.name,
                description: action.description,
                priority: action.priority,
                projectId: 1
            };
            return {
                ...state,
                posts: [...state.posts, newTask],
            };
        case NAME_SORT: {
            return {
                ...state,
                posts: [...state.posts.sort((a, b) => (a.name > b.name) ? 1 : ((b.name > a.name) ? -1 : 0))]
            }
        }
        case PRIORITY_SORT: {
            return {
                ...state,
                posts: [...state.posts.sort((a, b) => (a.priority > b.priority) ? 1 : ((b.priority > a.priority) ? -1 : 0))]
            }
        }
        /*case DELETE_TASK: {
            debugger;
            return {
                ...state,
                posts: state.posts.filter(function (task) {
                    return task.id !== action.id
                })
            }
        }*/
        default:
            return state;
    }
};

export default tasksReducer;

export const setNewTask = (name, description, priority) => ({type: SET_NEW_TASK, name, description, priority});
export const nameSortActionCreator = () => ({type: NAME_SORT});
export const prioritySortActionCreator = () => ({type: PRIORITY_SORT});

export const addNewTask = (name, description, priority) => (dispatch) => {
    priority = parseInt(priority, 10);
    addTask(name, description, 1, priority)
        .then((response => {
            if (response === null) {
                dispatch(setNewTask(name, description, priority));
            }
            else {
                alert("error");
            }
        }))
};
