import React, {Component} from 'react';
import style from './Projects.module.css';
import ProjectsBoard from './ProjectsBoard/ProjectsBoard';
import AddProject from './AddProject/AddProject';
import {withModalWindow} from "../../HOC/ModalWindow/ModalWindow";
import {initializeProjects} from "../../redux/projects-reducer";
import {connect} from "react-redux";

const AddProjectWindow = withModalWindow(AddProject);

class Projects extends React.Component {
    componentDidMount() {
        this.props.initializeProjects();
    }

    render() {
        if (!this.props.initialized) {
            return <div>no initialized projects</div>;
        }
        return (<div className={style.projectsLayout}>
            <div className={style.addProject}>
                <AddProjectWindow buttonName={"Добавить новый проект"}/>
            </div>
            <ProjectsBoard/>
        </div>);
    }
}

const mapStateToProps = (state) => ({
    initialized: state.projectsReducer.initialized
});

export default connect(mapStateToProps, {initializeProjects})(Projects);
