import React from 'react';
import {TaskInput} from "./../../Content/AddTask/TaskFrom/TaskInput/TaskInput";
import {Field, reduxForm} from 'redux-form';
import {connect} from "react-redux";
import {addNewProject} from "../../../redux/projects-reducer";

const AddProjectForm = ({handleSubmit, error}) => {
    return (
        <form onSubmit={handleSubmit}>
            <div><Field component={TaskInput} name={"name"} placeholder={"Название проекта *"} autoFocus={true} /></div>

            <button type="Submit" onSubmit={handleSubmit}>Добавить проект</button>
        </form>
    );
};

const ReduxProjectForm = reduxForm({ form: 'addProject'})(AddProjectForm);

const AddProject = (props) => {
    const onSubmit = (formData) => {
        props.addNewProject(formData.name);
    };
    return(
        <div>
            <h1 className={style.label}>Новый проект</h1>
            <ReduxProjectForm onSubmit={onSubmit} />
        </div>
    );
};

export default connect(null, {addNewProject})(AddProject);
