import React, {Component} from 'react';
import s from './TasksContent.module.css';
import TasksBoardContainer from "./TasksBoard/TasksBoardContainer";
import AddTaskContainer from "./AddTask/AddTaskContainer";
import {withRouter} from 'react-router-dom';
import {compose} from "redux";

class TasksContent extends React.Component {
    componentDidMount() {
        this.props.initializeTasks();
    }

    render() {
        if (!this.props.initialized) {
            return <div>no initialized tasks</div>;
        }
        return (<div className={s.layout}>
            <div className={s.addTaskContent}>
                <div className={s.title}>New task</div>
                <AddTaskContainer/>
            </div>
            <TasksBoardContainer/>
        </div>);
    }
}

export default compose(connect(null, {initializeTasks}), withRouter)(TasksContent);
